import React, { useState, useEffect } from 'react';
import { fetchYouTubeData } from '../api/youtubeApi'; // Your API for fetching YouTube data
const API_BASE = 'http://localhost:8000/youtube/';

const FetchDataForm = () => {
  const [channelUrl, setChannelUrl] = useState(''); // For YouTube channel URL input
  const [comments, setComments] = useState([]); // For storing fetched comments
  const [error, setError] = useState(null); // For error handling

  // Function to fetch comments from the backend
  const fetchComments = async () => {
    try {
      const response = await fetch(`${API_BASE}comments/`); // Adjust API URL if needed
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();

      // Validate if the response data is an array
      if (Array.isArray(data)) {
        setComments(data);
      } else {
        console.error('Expected an array, but received:', data);
        setComments([]); // Fallback to empty array
      }
    } catch (err) {
      console.error('Error fetching comments:', err);
      setError('Error fetching comments.');
    }
  };

  // Function to handle fetching YouTube data
  const handleFetch = async () => {
    try {
      await fetchYouTubeData(channelUrl); // Assuming this sends data to the backend
      alert('Data fetched successfully!');
      fetchComments(); // Fetch the stored comments after data is processed
    } catch (err) {
      alert('Error fetching YouTube data.');
    }
  };

  // Function to download comments as an Excel file
  const handleDownloadExcel = async () => {
    try {
      const response = await fetch(`${API_BASE}export-comments/`); // Adjust API URL if needed
      if (!response.ok) {
        throw new Error('Failed to download Excel file');
      }
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'comments.xlsx');
      document.body.appendChild(link);
      link.click();
      link.parentNode.removeChild(link);
    } catch (err) {
      alert('Error downloading the Excel file.');
    }
  };

  // Fetch comments when the component mounts
  useEffect(() => {
    fetchComments(); // Fetch comments on component mount
  }, []);

  return (
    <div>
      <h2>YouTube Comments</h2>

      {/* Input for YouTube Channel URL */}
      <input
        type="text"
        placeholder="Enter YouTube Channel URL"
        value={channelUrl}
        onChange={(e) => setChannelUrl(e.target.value)}
      />
      <button onClick={handleFetch}>Fetch Data</button>

      {/* Error display */}
      {error && <div style={{ color: 'red' }}>{error}</div>}

      {/* Comments Table */}
      <table border="1">
        <thead>
          <tr>
            <th>Comment ID</th>
            <th>Comment Text</th>
            <th>Author</th>
          </tr>
        </thead>
        <tbody>
          {comments.map((comment) => (
            <tr key={comment.comment_id}>
              <td>{comment.comment_id}</td>
              <td>{comment.text}</td>
              <td>{comment.author}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Button to download comments as Excel */}
      <button onClick={handleDownloadExcel}>Download Excel</button>
    </div>
  );
};

export default FetchDataForm;
